const hamburger=document.querySelector(".hamburger"),nav=document.querySelector(".nav"),list=document.querySelector(".list"),showcase=document.querySelector(".showcase"),header=document.querySelector(".header"),menu=document.querySelector(".menu"),allLinks=document.querySelectorAll(".link");function loopThroughLinks(){allLinks.forEach(function(e){e.classList.remove("list__link--active")})}function open(){nav.classList.toggle("nav--overlay"),list.classList.toggle("list--overlay"),hamburger.classList.toggle("hamburger--clicked"),header.classList.toggle("header--height"),menu.classList.toggle("menu--height")}allLinks.forEach(function(e){e.addEventListener("click",function(){loopThroughLinks(),e.classList.toggle("list__link--active")})}),hamburger.addEventListener("click",open),document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})});